            <div>
            <Form onSubmit= {e => e.preventDefault()}>
            <Form.Group controlId="formBasicEmail" >
                <Form.Label>Search By Name</Form.Label>
                <Form.Control type="text" placeholder="Enter Name" onChange={(e =>{props.updateSearch(e.target.value)})} />
            </Form.Group>
            </Form>
            </div>
        )
