import React from "react";
import API from "../utils/API.js";

class Directory extends React.Component{
    state = {
        name: "",
        role: "",
        manager: "",
        users: []
      }

      componentDidMount() {
        API
        .getUser()
        .then(res => this.setState({users: res.data.results}));
      }

      render() {
        return <UserTable users = {this.state.users}/>
      }
}


export default Directory;
